				{
					["trigger"] = {
						["type"] = "custom",
						["custom_type"] = "stateupdate",
						["debuffType"] = "HELPFUL",
						["event"] = "Health",
						["unit"] = "player",
						["custom"] = "function(s, e, ...)\n    if e == \"OPTIONS\" then\n        local cmapid = C_ChallengeMode.GetActiveChallengeMapID()\n        if cmapid and aura_env.bossname[1] then  \n            local max = select(3, C_Scenario.GetStepInfo())\n            if select(13, C_Scenario.GetCriteriaInfo(max)) then max = max-1 end\n            local affix = aura_env.affix\n            local cmap = aura_env.cmap\n            local level = aura_env.level \n            local haspb = aura_env.config.splits and aura_env.saved and aura_env.saved[cmap] and aura_env.saved[cmap][level] and aura_env.saved[cmap][level][affix]\n            for i=1, max do\n                local num = i\n                -- Fix for Mechagon, Tazavesh, Lower Kara, Upper Kara\n                num = (cmap == 370 and i+4) or (cmap == 392 and i+5) or (cmap == 227 and i+2) or (cmap == 234 and i+6) or num \n                \n                local name = aura_env.bossname[num]\n                local criteriaString = C_Scenario.GetCriteriaInfo(i)\n                for j=1, #aura_env.bossname do\n                    if string.find(criteriaString, aura_env.bossname[j]) then\n                        name = aura_env.bossname[j]\n                        break\n                    end\n                end\n                if cmap == 227 and num == 3 then -- Manual Opera Hall\n                    name = aura_env.config.karaname\n                end\n                name = WA_Utf8Sub(name, 20)\n                \n                if name and name ~= \"\" then\n                    s[i] = {\n                        name = name,\n                        index = i,\n                        show = true,\n                        done = false,\n                        changed = true,\n                    }\n                end\n                \n                if select(3, C_Scenario.GetCriteriaInfo(i)) then\n                    if s[i] and not s[i].time then\n                        local defeated = select(11, C_Scenario.GetCriteriaInfo(i))\n                        if defeated and defeated ~= 0 then\n                            local cur = select(2, GetWorldElapsedTime(1))-defeated or 0\n                            s[i].timer = cur\n                            s[i].name = aura_env.color..s[i].name\n                            local color = aura_env.ahead\n                            if haspb and aura_env.saved[cmap][level][affix][i] then\n                                local pb = aura_env.saved[cmap][level][affix][i]\n                                if cur == pb then\n                                    color = aura_env.equal\n                                    s[i].split = color..\"+-0\"\n                                else\n                                    color = cur <= pb and aura_env.ahead or aura_env.behind\n                                    local prefix = cur <= pb and \"-\" or \"+\"\n                                    local diff = cur-pb\n                                    if diff < 0 then diff = diff*-1 end\n                                    s[i].split = color..prefix..aura_env.formattime(diff)\n                                end\n                            end\n                            s[i].time = color..aura_env.formattime(cur)\n                            s[i].changed = true\n                            aura_env.bosstime[i] = cur\n                        end\n                    end\n                elseif haspb and s[i] and not s[i].time then\n                    s[i].time = aura_env.saved[cmap][level][affix][i] and aura_env.formattime(aura_env.saved[cmap][level][affix][i])\n                end\n                \n            end\n            return true\n        else\n            local names = {}\n            for i=1, 5 do\n                EJ_SelectInstance(721)\n                local name = EJ_GetEncounterInfoByIndex(i, 721)\n                name = WA_Utf8Sub(name, 20)\n                if name then\n                    names[i] = name\n                end\n            end\n            local killtime = 0\n            for i =1, 5 do\n                local addtime = math.random(240, 420)\n                killtime = killtime+addtime\n                s[i] = {\n                    name = i <= 3 and aura_env.color..names[i] or names[i],\n                    index = i, \n                    timer = killtime,\n                    \n                    time = \n                    aura_env.config.splits and (\n                        i == 1 and aura_env.behind..aura_env.formattime(killtime) or\n                        i == 2 and aura_env.equal..aura_env.formattime(killtime) or \n                        i == 3 and aura_env.ahead..aura_env.formattime(killtime) or \n                        aura_env.formattime(killtime)) or\n                    i <= 3 and aura_env.formattime(killtime),\n                    \n                    \n                    split = aura_env.config.splits and (\n                        i == 1 and aura_env.behind..\"+\"..aura_env.formattime(math.random(20, 60)) or \n                        i == 2 and aura_env.equal..\"+-0\" or \n                        i == 3 and aura_env.ahead..\"-\"..aura_env.formattime(math.random(20, 60))),\n                    \n                    show = true,\n                }\n                s[i].changed = true\n            end\n        end\n        return true\n    elseif e == \"CHALLENGE_MODE_COMPLETED\" then\n        local finish = select(3, C_ChallengeMode.GetCompletionInfo())\n        aura_env.bossname = {}   \n        -- add pb to aura_env.saved\n        local affix = aura_env.affix\n        local cmap = aura_env.cmap\n        local level = aura_env.level \n        if affix and cmap and level then\n            if not aura_env.saved then aura_env.saved = {} end\n            if not aura_env.saved[cmap] then aura_env.saved[cmap] = {} end\n            if not aura_env.saved[cmap][level] then aura_env.saved[cmap][level] = {} end\n            if not aura_env.saved[cmap][level][affix] then aura_env.saved[cmap][level][affix] = {} end\n            \n            if not aura_env.saved[cmap][level][affix][\"finish\"] or finish < aura_env.saved[cmap][level][affix][\"finish\"] then\n                aura_env.saved[cmap][level][affix][\"finish\"] = finish\n                for i=1, #s do\n                    aura_env.saved[cmap][level][affix][i] = s[i].timer                    \n                end\n            end\n        end\n        \n    elseif e == \"SCENARIO_CRITERIA_UPDATE\" then\n        local max = select(3, C_Scenario.GetStepInfo())\n        if select(13, C_Scenario.GetCriteriaInfo(max)) then max = max-1 end\n        local color = aura_env.ahead\n        for i=1, max do\n            if select(3, C_Scenario.GetCriteriaInfo(i)) then\n                if s[i] and ((aura_env.haspb and not s[i].split) or not s[i].time) then\n                    local cur = select(2, GetWorldElapsedTime(1))-select(11, C_Scenario.GetCriteriaInfo(i)) or 0\n                    s[i].timer = cur\n                    s[i].name = aura_env.color..s[i].name\n                    if aura_env.haspb and aura_env.store[i] then\n                        local pb = aura_env.store[i]\n                        if cur == pb then\n                            color = aura_env.equal\n                            s[i].split = color..\"+-0\"\n                        else\n                            color = cur <= pb and aura_env.ahead or aura_env.behind\n                            local prefix = cur <= pb and \"-\" or \"+\"\n                            local diff = cur-pb\n                            if diff < 0 then diff = diff*-1 end\n                            s[i].split = color..prefix..aura_env.formattime(diff)\n                        end\n                    end\n                    s[i].time = color..aura_env.formattime(cur)\n                    s[i].changed = true\n                    aura_env.bosstime[i] = cur\n                end\n            end\n        end\n        return true\n    elseif e == \"STATUS\" or e == \"CHALLENGE_MODE_START\" then\n        if select(3, C_ChallengeMode.GetCompletionInfo()) ~= 0 then\n            WeakAuras.ScanEvents(\"RELOE_BOSS_UPDATES\", 1, false)\n        else\n            aura_env.cmap = nil\n            local level, affixes = C_ChallengeMode.GetActiveKeystoneInfo()\n            aura_env.cmap = C_ChallengeMode.GetActiveChallengeMapID()\n            aura_env.affix = 0\n            aura_env.level = level\n            for _, v in pairs(affixes) do\n                if v == 9 or v == 10 then\n                    aura_env.affix = v\n                    break\n                end\n            end\n            \n            \n            local max = select(3, C_Scenario.GetStepInfo())\n            if max > 0 then\n                for _, v in pairs(s) do\n                    v.show = false\n                    v.changed = true\n                end\n            end\n            \n            aura_env.bossname = {}\n            aura_env.bosstime = {}\n            WeakAuras.ScanEvents(\"RELOE_BOSS_UPDATES\", 1, true)\n        end\n        return true\n    elseif e == \"RELOE_BOSS_UPDATES\" and ... and (not aura_env.last or GetTime() > aura_env.last+0.1) and not WeakAuras.IsOptionsOpen() then\n        local count, init= ...\n        aura_env.last = GetTime()        \n        local max = select(3, C_Scenario.GetStepInfo())\n        if select(13, C_Scenario.GetCriteriaInfo(max)) then max = max-1 end -- if last criteria is enemy forces\n        for _, v in pairs(s) do\n            v.show = false\n            v.changed = true\n        end\n        aura_env.setbossnames()\n        local cmap = init and C_ChallengeMode.GetActiveChallengeMapID() or aura_env.cmap\n        if count <= 8 and not WeakAuras.IsOptionsOpen() then -- check again in 2 seconds a few times to make sure data is correct\n            C_Timer.After(2, function()\n                    WeakAuras.ScanEvents(\"RELOE_BOSS_UPDATES\", count+1, init)\n            end)\n        end        \n        if max > 0 then \n            if not aura_env.cmap then -- setting variables for aura_env.saved saving\n                local level, affixes = C_ChallengeMode.GetActiveKeystoneInfo()\n                aura_env.cmap = C_ChallengeMode.GetActiveChallengeMapID()\n                aura_env.affix = 0\n                aura_env.level = level\n                for _, v in pairs(affixes) do\n                    if v == 9 or v == 10 then\n                        aura_env.affix = v\n                        break\n                    end\n                end\n            end\n            for k, v in pairs(s) do \n                if not C_Scenario.GetCriteriaInfo(k) then\n                    v.show = false\n                    v.changed = true\n                end\n            end\n            if select(13, C_Scenario.GetCriteriaInfo(max)) then max = max-1 end\n            local affix = aura_env.affix\n            local level = aura_env.level \n            aura_env.haspb = aura_env.config.splits and aura_env.saved and aura_env.saved[cmap] and aura_env.saved[cmap][level] and aura_env.saved[cmap][level][affix]\n            aura_env.store = aura_env.haspb and aura_env.saved[cmap][level][affix]\n            for i=1, max do\n                local num = i\n                -- Fix for Mechagon, Tazavesh, Lower Kara, Upper Kara\n                num = (cmap == 370 and i+4) or (cmap == 392 and i+5) or (cmap == 227 and i+2) or (cmap == 234 and i+6) or num \n                \n                local name = aura_env.bossname[num]\n                local criteriaString = C_Scenario.GetCriteriaInfo(i)\n                for j=1, #aura_env.bossname do\n                    if string.find(criteriaString, aura_env.bossname[j]) then\n                        name = aura_env.bossname[j]\n                        break\n                    end\n                end\n                if cmap == 227 and num == 3 then -- Manual Opera Hall\n                    name = aura_env.config.karaname\n                end\n                name = WA_Utf8Sub(name, 20)\n                \n                \n                if name and name ~= \"\" then\n                    s[i] = {\n                        name = name,\n                        index = i,\n                        show = true,\n                        changed = true,\n                        \n                    }\n                end\n                \n                if select(3, C_Scenario.GetCriteriaInfo(i)) then\n                    if s[i] and ((aura_env.haspb and not s[i].split) or not s[i].time) then\n                        local defeated = select(11, C_Scenario.GetCriteriaInfo(i))\n                        if defeated and defeated ~= 0 then\n                            local cur = select(2, GetWorldElapsedTime(1))-defeated or 0\n                            s[i].timer = cur\n                            s[i].name = aura_env.color..s[i].name\n                            local color = aura_env.ahead\n                            if aura_env.haspb and aura_env.store[i] then\n                                local pb = aura_env.store[i]\n                                if cur == pb then\n                                    color = aura_env.equal\n                                    s[i].split = color..\"+-0\"\n                                else\n                                    color = cur <= pb and aura_env.ahead or aura_env.behind\n                                    local prefix = cur <= pb and \"-\" or \"+\"\n                                    local diff = cur-pb\n                                    if diff < 0 then diff = diff*-1 end\n                                    s[i].split = color..prefix..aura_env.formattime(diff)\n                                end\n                            end\n                            s[i].time = color..aura_env.formattime(cur)\n                            s[i].changed = true\n                            aura_env.bosstime[i] = cur\n                        end\n                    end\n                elseif aura_env.haspb and s[i] and not s[i].time then\n                    s[i].time = aura_env.store[i] and aura_env.formattime(aura_env.store[i])\n                end\n            end\n            local aura_env = aura_env\n            C_Timer.After(0.1, function()\n                    if aura_env.bossname ~= {} then\n                        WeakAuras.ScanEvents(\"RELOE_SETBGHEIGHT\", #s, aura_env.region.height)\n                    else\n                        WeakAuras.ScanEvents(\"RELOE_SETBGHEIGHT\", 0, aura_env.region.height)\n                    end\n            end)\n            return true\n        end        \n    end\nend",
