						["custom"] = "function (allstates, event, ...)\n    if aura_env.config.disableSharedNote and aura_env.config.disablePersonalNote then\n        return false\n    end\n    if not (aura_env.config.enableWNoteDisabled or aura_env.Note.enabled) then\n        return false\n    end\n    \n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        if not aura_env.cleucheck then\n            return false\n        end\n        \n        local _, opt, _, _, _, _, _, _, _, _, _, spellID, spellName = ...\n        \n        local thisEvent = aura_env.eventTable[opt]\n        if not thisEvent then\n            return false\n        end\n        local condition = thisEvent .. spellID\n        if aura_env.checks[condition] then\n            local encounterStarted = aura_env.encounterStarted \n            local auraid = aura_env.id\n            aura_env.checks[condition] = aura_env.checks[condition] + 1\n            DebugPrint(\"CLEU\", condition, spellName, aura_env.checks[condition])\n            local option_w_counter = thisEvent .. \":\" .. spellID .. \":\" .. aura_env.checks[condition]\n            local condition_w_counter = aura_env.conditions[option_w_counter]\n            if condition_w_counter then\n                for _, k in ipairs(condition_w_counter) do\n                    table.insert(\n                        aura_env.timers,\n                        C_Timer.NewTimer(\n                            k.start < 0 and 0.1 or k.start,\n                            function()\n                                WeakAuras.ScanEvents(\n                                    \"ERT_NOTE_NEW\",\n                                    k.start,\n                                    k.line,\n                                    nil,\n                                    encounterStarted,\n                                    auraid\n                                )\n                            end\n                        )\n                    )\n                    DebugPrint(\"New CLEU Timer w/ counter\", option_w_counter, k.line, k.start)\n                end\n            end\n            local option_wo_counter = thisEvent .. \":\" .. spellID .. \":\" .. 0\n            local condition_wo_counter = aura_env.conditions[option_wo_counter]\n            if condition_wo_counter then\n                for _, k in ipairs(condition_wo_counter) do\n                    table.insert(\n                        aura_env.timers,\n                        C_Timer.NewTimer(\n                            k.start < 0 and 0.1 or k.start,\n                            function()\n                                WeakAuras.ScanEvents(\n                                    \"ERT_NOTE_NEW\",\n                                    k.start,\n                                    k.line,\n                                    nil,\n                                    encounterStarted,\n                                    auraid\n                                )\n                            end\n                        )\n                    )\n                    DebugPrint(\"New CLEU Timer w/o counter\", option_wo_counter, k.line, k.start)\n                end\n            end\n        end\n    elseif event == \"ERT_NOTE_PHASE\"\n    and (IsAddOnLoaded(\"ExRT\") or IsAddOnLoaded(\"MRT\"))\n    and ...\n    then\n        local opt, opt2 = ...\n        local opts = opt .. opt2\n        if not aura_env.checks[opts] or opt2 == aura_env.phase then\n            return false\n        end\n        DebugPrint(\"New Phase\", opt2)\n        aura_env.checks[opts] = aura_env.checks[opts] + 1\n        aura_env.phase = opt2\n        local conditionPhase = aura_env.conditions[opts]\n        if conditionPhase then\n            local encounterStarted = aura_env.encounterStarted \n            local auraid = aura_env.id\n            for _, k in ipairs(conditionPhase) do\n                table.insert(\n                    aura_env.timers,\n                    C_Timer.NewTimer(\n                        k.start < 0 and 0.1 or k.start,\n                        function()\n                            WeakAuras.ScanEvents(\n                                \"ERT_NOTE_NEW\",\n                                k.start,\n                                k.line,\n                                opt2,\n                                encounterStarted,\n                                auraid\n                            )\n                        end\n                    )\n                )\n                DebugPrint(\"New Phase Timer\", k.line, k.start)\n            end\n        end\n    elseif event == \"ERT_NOTE_NEW\" then\n        local start, line, phase, encounterStarted, id = ...\n        if encounterStarted == aura_env.encounterStarted\n        and (phase == nil or phase == aura_env.phase)\n        and aura_env.id == id\n        then\n            if start then\n                local warn = aura_env.warn\n                if start < 0 then\n                    warn = warn + start\n                end\n                table.insert(\n                    allstates,\n                    {\n                        show = true,\n                        changed = true,\n                        progressType = \"timed\",\n                        autoHide = true,\n                        duration = aura_env.config.warn or 15,\n                        expirationTime = GetTime() + warn,\n                        name = aura_env.format(line),\n                        type = \"timed\"\n                    }\n                )\n                DebugPrint(\"Show new timer\", GetTime() - aura_env.encounterStarted, line, warn, start)\n                return true\n            end\n        end\n    elseif event == \"ENCOUNTER_START\"\n    and (IsAddOnLoaded(\"ExRT\") or IsAddOnLoaded(\"MRT\"))\n    and ...\n    then\n        aura_env.timers = {}\n        aura_env.encounterStarted = GetTime()\n        aura_env.phase = 1\n        aura_env.conditions = {}\n        aura_env.checks = {}\n        aura_env.cleucheck = false\n        \n        aura_env.keyword[\"group:\" .. aura_env.MyGroup()] = true\n        aura_env.keyword[\"class:\" .. select(2, UnitClass(\"player\"))] = true\n        \n        local notes = {\n            not aura_env.config.disableSharedNote and aura_env.Note.Text1 or \"\",\n            not aura_env.config.disablePersonalNote and aura_env.Note.SelfText or \"\",\n        }\n        \n        local spec = GetSpecialization and GetSpecialization()\n        local role, _\n        if spec then\n            _, spec, _, _, role, _ = GetSpecializationInfo(spec)\n            \n            aura_env.keyword[\"role:\" .. role] = true\n            aura_env.keyword[\"spec:\" .. spec] = true\n        end\n        DebugPrint(\"spec\", spec)\n        DebugPrint(\"role\", role)\n        \n        for i = 1, 2 do\n            local text = notes[i]\n            \n            for line in text:gmatch(\"[^\\r\\n]+\") do\n                if spec then\n                    if role ~= \"HEALER\" then\n                        line = line:gsub(\"{[Hh]}.-{/[Hh]}\", \"\")\n                    end\n                    if role ~= \"TANK\" then\n                        line = line:gsub(\"{[Tt]}.-{/[Tt]}\", \"\")\n                    end\n                    if role ~= \"DAMAGER\" then\n                        line = line:gsub(\"{[Dd]}.-{/[Dd]}\", \"\")\n                    end\n                end\n                line = line:gsub(\"{(!?)[Gg](%d+)}(.-){/[Gg]}\", aura_env.GSUB_Group)\n                :gsub(\"{(!?)[Pp]:([^}]+)}(.-){/[Pp]}\", aura_env.GSUB_Player)\n                :gsub(\"{(!?)[Cc]:([^}]+)}(.-){/[Cc]}\", aura_env.GSUB_Class)\n                :gsub(\"{(!?)[Rr][Aa][Cc][Ee]:([^}]+)}(.-){/[Rr][Aa][Cc][Ee]}\", aura_env.GSUB_Race)\n                \n                local keywordFound = false\n                for k in pairs(aura_env.keyword) do\n                    if line:lower():match(k:lower()) then\n                        keywordFound = true\n                        break\n                    end\n                end\n                \n                if (not aura_env.config.onlyOwnShared and i == 1)\n                or (not aura_env.config.onlyOwnPersonal and i == 2)\n                or keywordFound\n                then\n                    local minute, sec, options = line:match(\"{time:(%d+)[:%.]?(%d*),?([^{}]*)}\")\n                    \n                    if minute == nil and sec == nil then\n                        minute, sec = line:match(\"%((%d+)[:%.]?(%d*)%)\") -- support (mn:sec) or (sec)\n                    end\n                    \n                    if sec == nil or sec == \"\" then\n                        sec = tonumber(minute)\n                        minute = 0\n                    else\n                        minute = tonumber(minute)\n                        sec = tonumber(sec)\n                    end\n                    \n                    if sec then\n                        local start = sec + (minute * 60) - aura_env.warn\n                        \n                        if options == nil or options == \"\" or options == \"p1\" then\n                            local phase = (options == \"p1\") and 1 or nil\n                            local encounterStarted = aura_env.encounterStarted \n                            local auraid = aura_env.id\n                            table.insert(\n                                aura_env.timers,\n                                C_Timer.NewTimer(\n                                    start < 0 and 0.1 or start,\n                                    function()\n                                        WeakAuras.ScanEvents(\n                                            \"ERT_NOTE_NEW\",\n                                            start,\n                                            line,\n                                            phase,\n                                            encounterStarted,\n                                            auraid\n                                        )\n                                    end\n                                )\n                            )\n                            DebugPrint(\"Timer programmed\", line, start, phase)\n                        end\n                        if not (options == \"\") and options then\n                            local opt, spellID, count = strsplit(\":\", options)\n                            spellID = spellID or \"\"\n                            if not aura_env.cleucheck then\n                                if opt == \"SCC\"\n                                or opt == \"SCS\"\n                                or opt == \"SAA\"\n                                or opt == \"SAR\"\n                                then\n                                    aura_env.cleucheck = true\n                                end\n                            end\n                            aura_env.conditions[options] = aura_env.conditions[options] or {}\n                            aura_env.checks[opt .. spellID] = 0\n                            table.insert(\n                                aura_env.conditions[options],\n                                {\n                                    start = start,\n                                    line = line,\n                                }\n                            )\n                            DebugPrint(\"Timer w/ options programmed\", line, start, options, opt, spellID, count)\n                        end\n                    end\n                end\n            end\n        end\n    elseif event == \"ENCOUNTER_END\" then\n        aura_env.conditions = {}\n        aura_env.checks = {}\n        aura_env.cleucheck = false\n        for _, v in pairs(allstates) do\n            v.show = false\n            v.changed = true\n        end\n        if aura_env.timers then\n            for i, v in ipairs(aura_env.timers) do\n                v:Cancel()\n                aura_env.timers[i] = nil\n            end\n        end\n        return true\n    end\nend",
