saved[cmap][level][affix] then aura_env.saved[cmap][level][affix] = {} end\n            \n            aura_env.before = aura_env.saved[cmap][level][affix][\"finish\"]\n            if not aura_env.saved[cmap][level][affix][\"finish\"] or time < aura_env.saved[cmap][level][affix][\"finish\"] then\n                aura_env.saved[cmap][level][affix][\"finish\"] = time\n            end\n        end        \n        -- printing in chat\n        if aura_env.config[\"print\"] then\n            local timer = (time/1000)  \n            local timeMS  = select(2, strsplit(\".\", (timer))) or select(2, strsplit(\".\", (GetTime()-timer)))\n            timeMS = (aura_env.decimalsF == 0 and 0) or (timeMS and string.sub(timeMS, 1, aura_env.decimalsF))\n            if timer > 0 and timeMS and timeMS ~= 0 then\n                timeMS = (\".%s\"):format(timeMS)\n            else\n                timeMS = \"\"\n            end\n            local current = aura_env.formattime(math.floor(timer))\n            local timertext = (\"%s%s\"):format(current, timeMS) or \"00:00\"\n            timertext = timertext..\"/\"..aura_env.formattime(aura_env.timelimit)\n            \n            if aura_env.config.ChestTimers ~= 3 then\n                \n                local chest = select(5, C_ChallengeMode.GetCompletionInfo())\n                aura_env.threeDD = (chest == 3 and aura_env.color..\"-\"..aura_env.formattime(aura_env.three-timer)..\"|r\")\n                or ((chest == 2 or aura_env.config.ChestTimers == 2) and \"|cFFFF0000+\"..aura_env.formattime(timer-aura_env.three)..\"|r\") or \"\"\n                aura_env.twoDD = ((chest == 2 or (chest == 3 and aura_env.config.ChestTimers == 2)) and aura_env.color..\"-\"..aura_env.formattime(aura_env.two-timer)..\"|r\")\n                or ((chest == 1 or aura_env.config.ChestTimers == 2) and \"|cFFFF0000+\"..aura_env.formattime(timer-aura_env.two)..\"|r\") or \"\"\n                aura_env.maxDD = ((chest == 1 or (chest > 1 and aura_env.config.ChestTimers == 2)) and aura_env.color..\"-\"..aura_env.formattime(aura_env.timelimit-timer)..\"|r\")\n                or (chest == 0 and \"|cFFFF0000+\"..aura_env.formattime(timer-aura_env.timelimit)..\"|r\") or \"\"\n                aura_env.threeDD, aura_env.twoDD, aura_env.maxDD = aura_env.threeDD ~= \"\" and \"3Chest:\"..aura_env.threeDD, aura_env.twoDD ~= \"\" and \"2Chest:\"..aura_env.twoDD, aura_env.maxDD ~= \"\" and \"1Chest:\"..aura_env.maxDD\n            else\n                aura_env.threeDD, aura_env.twoDD, aura_env.maxDD = \"\", \"\", \"\"\n            end\n            if select(4, C_ChallengeMode.GetCompletionInfo()) then\n                timertext = aura_env.color..timertext..\"|r\"\n            else\n                timertext = \"|cFFFF0000\"..timertext..\"|r\"\n            end\n            print(\"Finish Time: \"..timertext, aura_env.threeDD, aura_env.twoDD, aura_env.maxDD)\n        end\n        aura_env.timer = time/1000\n        local timeremain = aura_env.timelimit-aura_env.timer        \n        local chest = select(5, C_ChallengeMode.GetCompletionInfo())\n        \n        if not s[\"\"] then\n            s[\"\"] = {\n                show = true,\n                progressType = \"timed\",\n                duration = aura_env.timelimit,\n                expirationTime = timeremain+timenow,\n                chests = chest,\n                changed = true,\n            }\n        else\n            s[\"\"].expirationTime = timeremain+timenow\n            s[\"\"].chests = chest\n            s[\"\"].changed = true\n        end\n        return true\n    elseif e == \"CHALLENGE_MODE_START\" or e == \"STATUS\" then\n        local time = select(3, C_ChallengeMode.GetCompletionInfo())\n        local timenow = GetTime()\n        if time == 0 then\n            aura_env.completionplayed = false\n            aura_env.expiresplayed = false\n            aura_env.remainplayed = false\n            aura_env.finish = 0\n            aura_env.started = true\n            local mapID = C_ChallengeMode.GetActiveChallengeMapID()\n            if mapID then\n                aura_env.mapID = mapID\n            else\n                mapID = aura_env.mapID\n            end\n            local level, affixes = C_ChallengeMode.GetActiveKeystoneInfo()\n            aura_env.cmap = mapID\n            aura_env.affix = 0\n            aura_env.level = level\n            for _, v in pairs(affixes) do\n                if v == 9 or v == 10 then\n                    aura_env.affix = v\n                    break\n                end\n            end\n            if mapID and mapID ~= 0 then\n                aura_env.timer = select(2, GetWorldElapsedTime(1))\n                aura_env.timelimit  = select(3, C_ChallengeMode.GetMapUIInfo(mapID))          \n                local timeremain = aura_env.timelimit-aura_env.timer\n                aura_env.two = aura_env.timelimit*0.8\n                aura_env.three = aura_env.timelimit*0.6\n                \n                local chest = (aura_env.timer >= aura_env.timelimit and 0) or (aura_env.timer >= aura_env.two and 1) or (aura_env.timer >= aura_env.three and 2) or 3\n                s[\"\"] = {\n                    show = true,\n                    progressType = \"timed\",\n                    duration = aura_env.timelimit,\n                    expirationTime = timeremain+timenow,\n                    chests = chest,\n                    changed = true,\n                }\n                return true\n            end\n        elseif aura_env.cmap then -- if cmap already exists, STATUS event?\n            aura_env.timer = time/1000\n            aura_env.timelimit  = select(3, C_ChallengeMode.GetMapUIInfo(aura_env.cmap))\n            local timeremain = aura_env.timelimit-aura_env.timer        \n            local chest = select(5, C_ChallengeMode.GetCompletionInfo())\n            \n            if not s[\"\"] then\n                s[\"\"] = {\n                    show = true,\n                    progressType = \"timed\",\n                    duration = aura_env.timelimit,\n                    expirationTime = timeremain+time,\n                    chests = chest,\n                    changed = true,\n                }\n            else\n                s[\"\"].expirationTime = timeremain+time\n                s[\"\"].chests = chest\n                s[\"\"].changed = true\n            end\n        end\n    elseif e == \"FRAME_UPDATE\" and ((not aura_env.last) or aura_env.last < GetTime()-aura_env.config.Interval) and (select(3, C_ChallengeMode.GetCompletionInfo()) == 0 or aura_env.started) then\n        aura_env.last = GetTime()\n        aura_env.timer = select(2, GetWorldElapsedTime(1))\n        if (not aura_env.lasttimer) or aura_env.lasttimer ~= aura_env.timer then\n            if not aura_env.cmap then\n                local level, affixes = C_ChallengeMode.GetActiveKeystoneInfo()\n                aura_env.cmap = C_ChallengeMode.GetActiveChallengeMapID()\n                aura_env.affix = 0\n                aura_env.level = level\n                for _, v in pairs(affixes) do\n                    if v == 9 or v == 10 then\n                        aura_env.affix = v\n                        break\n                    end\n                end\n            end\n            aura_env.lasttimer = aura_env.timer\n            local timeremain = aura_env.timelimit-aura_env.timer\n            \n            local chest = \n            select(4, C_ChallengeMode.GetCompletionInfo()) and select(5, C_ChallengeMode.GetCompletionInfo()) \n            or (aura_env.timer >= aura_env.timelimit and 0) \n            or (aura_env.timer >= aura_env.two and 1) \n            or (aura_env.timer >= aura_env.three and 2) \n            or 3\n            \n            \n            if aura_env.config.timerexpires and aura_env.config.timerexpirespath and not aura_env.expiresplayed then\n                if timeremain < 0 then\n                    aura_env.expiresplayed = true\n                    PlaySoundFile(aura_env.config.timerexpirespath, \"MASTER\")\n                end\n            end\n            \n            if aura_env.config.timeremains and aura_env.config.timeremainspath and not aura_env.remainplayed then\n                if timeremain > 0 and timeremain < aura_env.config.timeremainstime then\n                    aura_env.remainplayed = true\n                    PlaySoundFile(aura_env.config.timeremainspath, \"MASTER\")\n                end\n            end\n            \n            local expires = timeremain+GetTime()\n            if not s[\"\"] then\n                s[\"\"] = {\n                    show = true,\n                    progressType = \"timed\",\n                    duration = aura_env.timelimit,\n                    expirationTime = expires,\n                    chests = chest,\n                    changed = true,\n                }\n                \n            else\n                s[\"\"].expirationTime = expires\n                s[\"\"].chests = chest\n                s[\"\"].changed = true\n            end\n            return true\n        end\n    end\nend    \n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
