						["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\"\n    and WeakAuras.IsOptionsOpen()\n    and aura_env.config.testMode then\n        for _, plate in pairs(C_NamePlate.GetNamePlates()) do\n            local unit = plate.namePlateUnitToken\n            local guid = UnitGUID(plate.namePlateUnitToken)\n            allstates[guid] = {\n                show = true,\n                changed = true,\n                unit = unit,\n                playerName = aura_env.config.showName and WA_ClassColorName(\"player\"),\n                counter = 1,\n            }\n        end\n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local subEvent = select(2, ...)\n        if aura_env.args[subEvent] then --SPELL_INTERRUPT / SPELL_CAST_SUCCESS\n            local guid, markNum, spellId = aura_env.args[subEvent](...)\n            if aura_env.assignments[markNum]\n            and aura_env.spellIds[markNum] == spellId then\n                if aura_env.counter[guid] then\n                    aura_env.counter[guid] = aura_env.counter[guid] + 1\n                else\n                    aura_env.counter[guid] = 2\n                end\n                local limit = #aura_env.assignments[markNum]\n                if aura_env.counter[guid] > limit then\n                    aura_env.counter[guid] = 1\n                end\n                local counter = aura_env.counter[guid]\n                local isMyAssignment = aura_env.assignments[markNum] and aura_env.assignments[markNum][counter] and aura_env.assignments[markNum][counter][WeakAuras.me]\n                    if isMyAssignment then\n                        PlaySoundFile(aura_env.sound[aura_env.config.specifiedSound], \"MASTER\")\n                    end\n                    local state = allstates[guid]\n                    if state then\n                        local assignedUnit = aura_env.orderedAssignments[markNum][counter][1]\n                        state.changed = true\n                        state.isCasting = false\n                        state.counter = counter\n                        state.playerName = aura_env.config.showName and (UnitExists(assignedUnit) and WA_ClassColorName(assignedUnit) or assignedUnit)\n                        state.isMyAssignment = isMyAssignment\n                        return true\n                    end\n                end\n            elseif subEvent == \"SPELL_CAST_START\" then\n                local guid = select(4, ...)\n                local mark =  select(7, ...)\n                local markNum = aura_env.convert[mark]\n                local spellId = select(12, ...)\n                if aura_env.assignments[markNum]\n                and aura_env.spellIds[markNum] == spellId then\n                    local counter = aura_env.counter[guid]\n                    local isMyAssignment = aura_env.assignments[markNum] and aura_env.assignments[markNum][counter] and aura_env.assignments[markNum][counter][WeakAuras.me]\n                    if isMyAssignment then\n                        PlaySoundFile(aura_env.castsound[aura_env.config.specifiedCastSound], \"MASTER\")\n                    end\n                    local state = allstates[guid]\n                    if state then\n                        state.isCasting = isMyAssignment\n                        state.changed = true\n                        return true\n                    end\n                end\n            end\n        elseif event == \"NAME_PLATE_UNIT_ADDED\" and ... then\n            local unit = ...\n            local markNum = GetRaidTargetIndex(unit)\n            if aura_env.assignments[markNum] then\n                local guid = UnitGUID(unit)\n                if not aura_env.counter[guid] then\n                    aura_env.counter[guid] = 1\n                end\n                local counter = aura_env.counter[guid]\n                local isMyMark = aura_env.myAssignments[markNum]\n                if isMyMark or aura_env.config.raidLeaderMode then\n                    local spellId = select(9, UnitCastingInfo(unit))\n                    local isMyAssignment = aura_env.assignments[markNum] and aura_env.assignments[markNum][counter] and aura_env.assignments[markNum][counter][WeakAuras.me]\n                    local assignedUnit = aura_env.orderedAssignments[markNum][counter][1]\n                    allstates[guid] = {\n                        show = true,\n                        changed = true,\n                        unit = unit,\n                        counter = counter,\n                        markNum = markNum,\n                        playerName = aura_env.config.showName and (UnitExists(assignedUnit) and WA_ClassColorName(assignedUnit) or assignedUnit),\n                        isCasting = aura_env.spellIds[markNum] == spellId and isMyAssignment,\n                        isMyAssignment = isMyAssignment\n                    }\n                    return true\n                end\n            end\n        elseif event == \"NAME_PLATE_UNIT_REMOVED\" and ... then\n            local unit = ...\n            local guid = UnitGUID(unit)\n            if guid then\n                local state = allstates[guid]\n                if state then\n                    state.changed = true\n                    state.show = false\n                    return true\n                end\n            end\n        elseif event == \"INSTANCE_ENCOUNTER_ENGAGE_UNIT\" then\n            for i = 1, 10 do\n                local unit = \"boss\"..i\n                local guid = UnitGUID(unit)\n                if not guid then break end\n                local markNum = GetRaidTargetIndex(unit)\n                if markNum\n                and aura_env.assignments[markNum]\n                and not aura_env.counter[guid] then\n                    aura_env.counter[guid] = 1\n                end\n            end\n        elseif event == \"ENCOUNTER_START\" then\n            if (IsAddOnLoaded(\"ExRT\") or IsAddOnLoaded(