                        backupsList[name] = nil\n                                else\n                                    counter = counter + 1\n                                    aura_env.assignments[markNum][counter] = {}\n                                    aura_env.orderedAssignments[markNum][counter] = {}\n                                end\n                                aura_env.assignments[markNum][counter][name] = true\n                                table.insert(aura_env.orderedAssignments[markNum][counter], name)\n                                if name == myName then\n                                    aura_env.myAssignments[markNum] = true\n                                end\n                            end\n                        end\n                    end\n                    if line == \"startLine\" then\n                        betweenLine = true\n                    end\n                end\n            end\n        end\n        if event == \"RAID_TARGET_UPDATE\" or event == \"ENCOUNTER_START\" then\n            for _, state in pairs(allstates) do\n                state.show = false\n                state.changed = true\n            end\n            for _, plate in pairs(C_NamePlate.GetNamePlates()) do\n                local unit = plate.namePlateUnitToken\n                local guid = UnitGUID(unit)\n                local markNum = GetRaidTargetIndex(unit)\n                if aura_env.assignments[markNum] then\n                    if not aura_env.counter[guid] then\n                        aura_env.counter[guid] = 1\n                    end\n                    local counter = aura_env.counter[guid]\n                    local isMyMark = aura_env.myAssignments[markNum]\n                    if isMyMark or aura_env.config.raidLeaderMode then\n                        local assignedUnit = aura_env.orderedAssignments[markNum][counter][1]\n                        local isMyAssignment = aura_env.assignments[markNum] and aura_env.assignments[mark