						["unit"] = "player",
						["custom"] = "function(allstates, event, ...)\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local timestamp, subEvent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags, destGUID, destName, destFlags, destRaidFlags, spellID, _,_, auraType = ...\n        if subEvent == \"SPELL_AURA_APPLIED\"\n        and spellID == 381253 then\n            \n            local aura_env = aura_env\n            if not aura_env.time then\n                aura_env.players = {}\n                aura_env.time = timestamp\n                aura_env.counter = aura_env.counter + 1\n                C_Timer.After(0.1, function() WeakAuras.ScanEvents(\"CAUSESE_TERROS_A\", aura_env.counter) end)\n            end\n            \n            if timestamp-aura_env.time > 1 then\n                aura_env.players = {}\n                aura_env.time = timestamp\n                aura_env.counter = aura_env.counter + 1\n                C_Timer.After(0.1, function() WeakAuras.ScanEvents(\"CAUSESE_TERROS_A\", aura_env.counter) end)\n            end\n            \n            table.insert(aura_env.players, destName)\n            \n        end\n        \n    elseif event == \"CAUSESE_TERROS_A\" and ... then\n        \n        local config = aura_env.config\n        local position = aura_env.LSpec.positionTable\n        \n        if config.meleeSide == 1 then\n            table.sort(aura_env.players, function(a, b) return aura_env.order[position[WeakAuras.SpecForUnit(a)]] < aura_env.order[position[WeakAuras.SpecForUnit(b)]] end)\n        else\n            table.sort(aura_env.players, function(a, b) return aura_env.order[position[WeakAuras.SpecForUnit(a)]] > aura_env.order[position[WeakAuras.SpecForUnit(b)]] end)\n        end\n        \n        if config.customRatio then\n            --MRT assignments\n            local count = ...\n            if aura_env.assignments[count] then\n                local _, numLeft = unpack(aura_env.assignments[count])\n                for index, player in ipairs(aura_env.players) do\n                    if player == WeakAuras.me then\n                        \n                        local spell = C_UnitAuras.GetPlayerAuraBySpellID(381253)\n                        local flag = tonumber(numLeft) >= index\n                        allstates[player] = {\n                            show = true,\n                            changed = true,\n                            progressType = \"timed\",\n                            duration = spell.duration,\n                            expirationTime = spell.expirationTime,\n                            autoHide = true,\n                            assignment = flag,\n                            name = config.meleeSide == 1 and ((flag and \"LEFT\") or \"RIGHT\") or ((not flag and \"RIGHT\") or \"LEFT\"),\n                        }\n                        return true\n                    end\n                end\n            end\n        else\n            --Regular assignments\n            local numAssignments = #aura_env.players\n            for index, player in ipairs(aura_env.players) do\n                if player == WeakAuras.me then\n                    local numLeft = floor((numAssignments-config.ratio+2)/2)\n                    local flag = numLeft >= index\n                    local spell = C_UnitAuras.GetPlayerAuraBySpellID(381253)\n                    allstates[player] = {\n                        show = true,\n                        changed = true,\n                        progressType = \"timed\",\n                        duration = spell.duration,\n                        expirationTime = spell.expirationTime,\n                        autoHide = true,\n                        assignment = flag,\n                        name = config.meleeSide == 1 and ((flag and \"LEFT\") or \"RIGHT\") or ((not flag and \"RIGHT\") or \"LEFT\"),\n                    }\n                    return true\n                end\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        \n        aura_env.counter = 0\n        aura_env.time = nil\n        aura_env.players = {}\n        \n        if (IsAddOnLoaded(\"ExRT\") or IsAddOnLoaded(\"MRT\"))\n        and VExRT\n        and VExRT.Note\n        and VExRT.Note.Text1 then\n            local note = VExRT.Note.Text1\n            local betweenLine = false\n            aura_env.assignments = {}\n            for line in note:gmatch('[^\\r\\n]+') do\n                if strtrim(line) == \"terrosEnd\" then\n                    betweenLine = false\n                end\n                if betweenLine then\n                    table.insert(aura_env.assignments, aura_env.split(line, \":\"))\n                end\n                if strtrim(line) == \"terrosStart\" then\n                    betweenLine = true\n                end\n            end\n        end\n    end\nend",
