					["custom"] = "aura_env.finishforces = aura_env.config[\"Decimals\"] ~= 5\naura_env.pullcount = aura_env.config[\"CountDisplay\"] ~= 3\naura_env.countD = aura_env.config[\"CountDisplay\"]\naura_env.remaining = aura_env.config[\"Remaining\"]\naura_env.display = aura_env.config[\"Display\"]\naura_env.totalC = aura_env.config[\"totalC\"]\naura_env.total = aura_env.total or 0\naura_env.done = aura_env.done or false\naura_env.update = false\naura_env.total = 0\n\nlocal c = aura_env.config.color\nlocal col = {}\n\nfor i=1, 4 do\n    if i == 4 then \n        col[i] = string.format(\"%x\", c[i] *255)\n    else\n        col[i] = string.format(\"%x\", c[i] *255)\n    end\n    if string.len(col[i]) == 1 then\n        col[i] = \"0\"..col[i]\n    end\nend\n\naura_env.color = \"|c\"..col[4]..col[1]..col[2]..col[3]\n\naura_env.GetProgress = function()\n    local steps = select(3, C_Scenario.GetStepInfo())\n    if not steps or steps <= 0 then\n        return\n    end\n    local _, _, _, _, total, _, _, current = C_Scenario.GetCriteriaInfo(steps)\n    if current then\n        current = tonumber(string.sub(current, 1, string.len(current) - 1)) or 0\n        local percent = current / total * 100\n        if percent then\n            percent = (percent <= 0 and 0) or (percent > 100 and 100) or percent\n            return percent, current..\" / \"..total, current, total\n        end\n    end\n    return false\nend\naura_env.GetProgress()\n\naura_env.MDT = MDT\n\naura_env.formattime = function(time, color)\n    color = color or \"|cFFFFFFFF\"\n    local timeMin = math.floor(time / 60)\n    local timeSec = math.floor(time - (timeMin*60))\n    if timeMin < 10 then\n        timeMin = (\"0%d\"):format(timeMin)\n    end\n    if timeSec < 10 then\n        timeSec = (\"0%d\"):format(timeSec)\n    end\n    return (color..\"%s:%s|r\"):format(timeMin, timeSec)\nend\n\naura_env.getoptionstext = function()\n    local count = 175\n    local max = 250\n    local pullC = 10\n    local perc = \"70.00%\"\n    local percC = \"4.00%\"\n    local percC2 = \"74.00%\"\n    local text1, text2, text3, text4 = \"\", \"\", \"\", \"\"\n    local dis = aura_env.config[\"Display\"]\n    if dis == 1 or dis == 2 then\n        text1 = perc\n        if aura_env.pullcount then\n            if aura_env.countD == 1 then\n                text2 = \"(+\"..percC..\")\"\n            else\n                text2 = \"(\"..percC2..\")\"\n            end\n        end\n        -- percent shit\n    end\n    if dis == 1 or dis == 3 then\n        if aura_env.remaining then\n            if aura_env.totalC then\n                text3 = max-count..\"/\"..max\n            else\n                text3 = max-count\n            end\n            if aura_env.pullcount then\n                if aura_env.countD == 1 then\n                    text4 = \"(-\"..pullC..\")\"\n                else\n                    text4 = \"(\"..max-count-pullC..\")\"\n                end\n            end\n        else\n            if aura_env.totalC then\n                text3 = count..\"/\"..max\n            else\n                text3 = count\n            end\n            if aura_env.pullcount then\n                if aura_env.countD == 1 then\n                    text4 = \"(+\"..pullC..\")\"\n                else\n                    text4 = \"(\"..count+pullC..\")\"\n                end\n            end\n        end\n    end\n    return text1, text2, text3, text4\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
