						["custom_hide"] = "custom",
						["custom"] = "function(event,prefix,text,channel,sender)\n    if not aura_env.config.disabled then\n        if event == aura_env.dropDownValues[aura_env.config.event] and not aura_env.fired then\n            if aura_env.myVersion then\n                C_ChatInfo.SendAddonMessage(\"CAUSESE_REQUEST\", aura_env.myVersion, \"RAID\")\n            end\n        end\n        if event == \"CHAT_MSG_ADDON\" then\n            if sender and prefix and channel and text then\n                if prefix == \"CAUSESE_REQUEST\" and channel == \"RAID\" then\n                    local name, server = UnitFullName(\"player\")\n                    if name and server and sender ~= name..\"-\"..server then\n                        local convertedText = tonumber(text)\n                        if aura_env.myVersion and convertedText and aura_env.myVersion > convertedText then\n                            local aura_env = aura_env\n                            C_Timer.After(0.1, function() C_ChatInfo.SendAddonMessage(\"CAUSESE_SEND\", aura_env.myVersion, \"WHISPER\", sender) end)\n                        end\n                    end\n                end\n                if prefix == \"CAUSESE_SEND\" and channel == \"WHISPER\" and not aura_env.fired then\n                    if aura_env.myVersion and UnitInRaid(strsplit(\"-\", sender)) then\n                        local convertedText = tonumber(text)\n                        if not aura_env.highestVersion then\n                            aura_env.highestVersion = convertedText\n                            local aura_env = aura_env\n                            C_Timer.After(0.1, function() \n                                    local msg = \"|cFFFF0000Your|r Vault of the Incarnates|r|cFFFF0000 weakauras|n are atleast |r\"..aura_env.highestVersion-aura_env.myVersion..\" versions|cFFFF0000 out of date.|r|n|nUpdate link:\"\n                                    local link = \"https://www.wago.io/dfraid1\"\n                                    if aura_env.config.diff <= (aura_env.highestVersion - aura_env.myVersion) then\n                                        StaticPopupDialogs[\"CAUSESE_RAID\"] = {\n                                            text = msg,\n                                            button1 = \"OK\",\n                                            showAlert = true,\n                                            hasEditBox = true,\n                                            editBoxWidth = 250,\n                                            OnShow = function (self, data)\n                                                self.editBox:ClearFocus()\n                                                self.editBox:SetText(link)\n                                                self.editBox:HighlightText()\n                                                self.editBox:SetAutoFocus(false)\n                                            end,\n                                            whileDead = true,\n                                            hideOnEscape = true,\n                                            preferredIndex = 3,\n                                        }\n                                        StaticPopup_Show(\"CAUSESE_RAID\") aura_env.fired = true end \n                                    if aura_env.config.print then print(msg..link) end end)\n                        elseif aura_env.highestVersion and aura_env.highestVersion < convertedText then\n                            aura_env.highestVersion = convertedText\n                        end\n                    end\n                end\n            end\n        end\n    end\nend",
