			["customText"] = "function()\n    \n    aura_env.finish = select(3, C_ChallengeMode.GetCompletionInfo())\n    local timer = (aura_env.finish ~= 0 and aura_env.finish/1000) or aura_env.timer or 0    \n    local timeMS  = select(2, strsplit(\".\", (timer))) or select(2, strsplit(\".\", (GetTime()-timer)))\n    local timertext = \"\"\n    \n    \n    local split\n    \n    if aura_env.finish ~= 0 then\n        timeMS = (aura_env.decimalsF == 0 and 0) or (timeMS and string.sub(timeMS, 1, aura_env.decimalsF))\n        if (aura_env.start or timer > 0) and timeMS and timeMS ~= 0 then\n            timeMS = (\".%s\"):format(timeMS)\n        else\n            timeMS = \"\"\n        end\n        if aura_env.config.ChestTimers ~= 3 then\n            local current = aura_env.formattime(math.floor(timer))\n            timertext = (\"%s%s\"):format(current, timeMS) or \"00:00\"\n            timertext = timertext..\"/\"..aura_env.formattime(aura_env.timelimit)\n            local chest = select(5, C_ChallengeMode.GetCompletionInfo())\n            aura_env.threeD = (chest == 3 and aura_env.color..\"-\"..aura_env.formattime(aura_env.three-timer)..\"|r\")\n            or ((chest == 2 or aura_env.config.ChestTimers == 2) and \"|cFFFF0000+\"..aura_env.formattime(timer-aura_env.three)..\"|r\") or \"\"\n            aura_env.twoD = ((chest == 2 or (chest == 3 and aura_env.config.ChestTimers == 2)) and aura_env.color..\"-\"..aura_env.formattime(aura_env.two-timer)..\"|r\")\n            or ((chest == 1 or aura_env.config.ChestTimers == 2) and \"|cFFFF0000+\"..aura_env.formattime(timer-aura_env.two)..\"|r\") or \"\"\n            aura_env.maxD = ((chest == 1 or (chest > 1 and aura_env.config.ChestTimers == 2)) and aura_env.color..\"-\"..aura_env.formattime(aura_env.timelimit-timer)..\"|r\")\n            or (chest == 0 and \"|cFFFF0000+\"..aura_env.formattime(timer-aura_env.timelimit)..\"|r\") or \"\"\n        else\n            aura_env.threeD, aura_env.twoD, aura_env.maxD = \"\", \"\", \"\"\n        end        \n        \n        if select(4, C_ChallengeMode.GetCompletionInfo()) then\n            timertext = aura_env.color..timertext..\"|r\"\n        else\n            timertext = \"|cFFFF0000\"..timertext..\"|r\"\n        end\n        \n        \n        local affix = aura_env.affix\n        local cmap = aura_env.cmap\n        local level = aura_env.level \n        local haspb = aura_env.config.splits and aura_env.saved and aura_env.saved[cmap] and aura_env.saved[cmap][level] and aura_env.saved[cmap][level][affix] and aura_env.saved[cmap][level][affix][\"finish\"]\n        if haspb and aura_env.before then       \n            local cur = aura_env.finish\n            local pb = aura_env.before\n            if cur == pb then\n                split = \"|cFFFFEA00+-0\"\n            else\n                local prefix = cur <= pb and \"|cFF00FF00-\" or \"|cFFFF0000+\"\n                local diff = (cur-pb)/1000\n                if diff < 0 then diff = diff*-1 end\n                split = prefix..aura_env.formattime(diff, true)\n            end\n        end\n        \n    else\n        if aura_env.config.ChestTimers ~= 3 then\n            aura_env.threeD = (aura_env.timer < aura_env.three and aura_env.formattime(aura_env.three-aura_env.timer)) or \"\"\n            aura_env.twoD = (aura_env.timer < aura_env.two and (aura_env.timer >= aura_env.three  or aura_env.config.ChestTimers == 2) and aura_env.formattime(aura_env.two-aura_env.timer))  or \"\"\n            aura_env.maxD = (aura_env.timer < aura_env.timelimit and (aura_env.timer >= aura_env.two  or aura_env.config.ChestTimers == 2) and aura_env.formattime(aura_env.timelimit-aura_env.timer)) \n            or (aura_env.timer >= aura_env.timelimit and \"|cFFFF0000+\"..aura_env.formattime(aura_env.timer-aura_env.timelimit)..\"|r\")\n            or \"\"\n        else\n            aura_env.threeD, aura_env.twoD, aura_env.maxD = \"\", \"\", \"\"\n        end\n        \n        local current = aura_env.formattime(timer)\n        timertext = current..\"/\"..aura_env.formattime(aura_env.timelimit)\n        \n    end\n    if WeakAuras.IsOptionsOpen() then\n        if timer == 0 then \n            timertext = \"22:00/38:00\"\n        end\n        if aura_env.config.ChestTimers and aura_env.threeD == \"\" then\n            aura_env.threeD = \"00:48\"\n            aura_env.twoD = \"08:24\"\n            aura_env.maxD = \"16:00\"\n        end\n        if aura_env.config.splits and not split then\n            split = \"|cFF00FF00-\"..aura_env.formattime(134, true)\n        end\n    end\n    return timertext, split, aura_env.threeD, aura_env.twoD, aura_env.maxD\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
